import React, { createContext, useContext, useState, useRef } from 'react';
import { dataIngestionAPI } from './api';

const DataIngestionContext = createContext();

export const useDataIngestion = () => {
  const context = useContext(DataIngestionContext);
  if (!context) {
    throw new Error('useDataIngestion must be used within a DataIngestionProvider');
  }
  return context;
};

// Cache para almacenar respuestas de API
const apiCache = new Map();
const CACHE_DURATION = 30000; // 30 segundos

// Función para crear una clave de cache
const createCacheKey = (method, params = {}) => {
  return `${method}_${JSON.stringify(params)}`;
};

// Función para verificar si el cache es válido
const isCacheValid = (cacheEntry) => {
  return cacheEntry && (Date.now() - cacheEntry.timestamp) < CACHE_DURATION;
};

export const DataIngestionProvider = ({ children }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const ingestData = async (data) => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.ingestData(data);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getData();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getStatistics = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getStatistics();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getSchema = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getSchema();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getTiradoresStatistics = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getTiradoresStatistics();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getSesionesStatistics = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getSesionesStatistics();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getAnalisisCompleto = async (params = {}) => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getAnalisisCompleto(params);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getTiradores = async (params = {}) => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getTiradores(params);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const addManualEntry = async (data) => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.addManualEntry(data);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getDataSchema = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.getDataSchema();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const deleteAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dataIngestionAPI.deleteAllData();
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const value = {
    loading,
    error,
    ingestData,
    getData,
    getStatistics,
    getSchema,
    getTiradoresStatistics,
    getSesionesStatistics,
    getAnalisisCompleto,
    getTiradores,
    addManualEntry,
    getDataSchema,
    deleteAllData,
  };

  return (
    <DataIngestionContext.Provider value={value}>
      {children}
    </DataIngestionContext.Provider>
  );
};
